import styled from 'styled-components';
import AttachFileIcon from '@mui/icons-material/AttachFile';
import SendIcon from '@mui/icons-material/Send';
import { useRef, useState } from 'react';


const MainContainer = styled.div`
  display: flex;
  border: 1px solid black;
  width: 70vw;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
`;
const InputContainer = styled.form`
  display: flex;
  justify-content: space-around;
  border: 1px solid rgb(207, 205, 202);
  align-items: center;
  border-radius: 100px;
  height: 40px;
  width: 600px;
  margin: 20px 0;
`;

const Input = styled.input`
  font-size: 15px;
  color: ${({ theme }) => theme.color};
  width: 500px;
  border: none;
  background: ${({ theme }) => theme.background}
`;

const NameBox = styled.div`
  display: flex;
  justify-content: center;
  height: 55px;
  width: 100%;
`;

const MessagesBox = styled.div`
  display: flex;
  justify-content: center;
  width: 100%;
  height: 100%;
  box-shadow: inset 0 0 10px #000000;
`;

const SendButton = styled.input.attrs({ type: 'submit'})`
  display: flex;
  justify-content: center;
  align-items: center;
  border: none;
  background: transparent;
`;
const Message = styled.p`
  color: ${({ theme }) => theme.color};
`


const Conversations: React.FC<{}> = () => {
  const [messages, setMessages] = useState<Array<string>>([]);

  const messagesInput = useRef<HTMLInputElement>(null);

  const sendMessage = (): void => {
    let input: HTMLInputElement = messagesInput.current!;
    setMessages([...messages, input.value]);
    input.value = '';
    input.focus();
  }

  return (
    <MainContainer>
      <NameBox></NameBox>
      <MessagesBox>
        {messages.map((message) => (
          <Message>{message}</Message>
        ))}
      </MessagesBox>
      <InputContainer>
        <AttachFileIcon style={{color: `grey`}}/>
        {/* <Input ref={messagesInput} autoFocus /> */}
        {/* <SendButton onSubmit={() => sendMessage()}><SendIcon /></SendButton> */}
      </InputContainer>
    </MainContainer>
  )
}
export default Conversations;
